import React from 'react';
import PropTypes from 'prop-types';

const Rating = ({ value, text, color }) => {
    return (
        <div className="rating">
            <span>
                <i className={value > 1 ? 'fas fa-star' : value >= 0.5 ?
                    'fas fa-star-half-alt' : 'far fa-star'} style={{ color }}>

                </i>
            </span>
            <span>
                <i className={value > 2 ? 'fas fa-star' : value >= 1.5 ?
                    'fas fa-star-half-alt' : 'far fa-star'} style={{ color }}>

                </i>
            </span>
            <span>
                <i className={value > 3 ? 'fas fa-star' : value >= 2.5 ?
                    'fas fa-star-half-alt' : 'far fa-star'} style={{ color }}>

                </i>
            </span>
            <span>
                <i className={value > 4 ? 'fas fa-star' : value >= 3.5 ?
                    'fas fa-star-half-alt' : 'far fa-star'} style={{ color }}>

                </i>
            </span>
            <span>
                <i className={value > 5 ? 'fas fa-star' : value >= 4.5 ?
                    'fas fa-star-half-alt' : 'far fa-star'} style={{ color }}>

                </i>
            </span>
            <span>{text && text}</span>
        </div>
    );
};
// Allows you to set default props
Rating.defaultProps = {
    color: '#f8e825 '
}

// Set proptypes
Rating.propTypes = {
    text: PropTypes.string.isRequired,
    color: PropTypes.string
}

export default Rating;
